mapper.select:
SELECT USUARIOS.ID_USUARIO AS ID_USUARIO, MAX(ID_PEDIDO) AS MAX_ID_PEDIDO FROM CAPITOLE.USUARIOS AS USUARIOS LEFT JOIN CAPITOLE.PEDIDOS AS PEDIDOS ON USUARIOS.ID_USUARIO = PEDIDOS.ID_USUARIO WHERE 1=1
AND PEDIDOS.ID_TIENDA = #{idStore}
GROUP BY USUARIOS.ID_USUARIO ORDER BY ID_USUARIO

WITH MAX_ORDER AS(SELECT MAX(PEDIDOS.TOTAL) AS TOTAL FROM CAPITOLE.PEDIDOS INNER JOIN CAPITOLE.USUARIOS AS USUARIOS ON USUARIOS.ID_USUARIO = PEDIDOS.ID_USUARIO INNER JOIN CAPITOLE.TIENDAS AS TIENDAS ON TIENDAS.ID_TIENDA = PEDIDOS.ID_TIENDA WHERE 1=1
) SELECT MAX(PEDIDOS.TOTAL) AS MAX_TOTAL, PEDIDOS.ID_PEDIDO AS ID_PEDIDIO, PEDIDOS.ID_USUARIO AS ID_USUARIO, PEDIDOS.ID_TIENDA AS ID_TIENDA, PEDIDOS.FECHA AS FECHA, PEDIDOS.TOTAL, PEDIDOS.SUBTOTAL, PEDIDOS.DIRECCION, PEDIDOS.CP, PEDIDOS.POBLACION, PEDIDOS.PAISCC2,USUARIOS.NOMBRE AS NOMBRE_USU, USUARIOS.APELLIDO1, USUARIOS.APELLIDO2 FROM CAPITOLE.PEDIDOS INNER JOIN CAPITOLE.USUARIOS AS USUARIOS ON USUARIOS.ID_USUARIO = PEDIDOS.ID_USUARIO INNER JOIN CAPITOLE.TIENDAS AS TIENDAS ON TIENDAS.ID_TIENDA = PEDIDOS.ID_TIENDA INNER JOIN MAX_ORDER ON PEDIDOS.TOTAL = MAX_ORDER.TOTAL WHERE 1=1
LIMIT 0,1
